		<script>			function OpWin(theBg,bgWidth,bgHeight,xOffset,yOffset,tableHeight,theName)			{			xsize = bgWidth;			ysize = bgHeight;			ScreenWidth = screen.width;			ScreenHeight = screen.height;			tmpName = Math.round(Math.random()*100000);			xpos = (ScreenWidth/2)-(xsize/2)+xOffset;			ypos = (ScreenHeight/2)-(ysize/2)-32+yOffset;				 NewWindow=window.open("",tmpName,"height="+ysize+",width="+xsize+",scrollbars=no,resizable=no,top="+ypos+",left="+xpos+"");				 NewWindow.document.write ("<html><head><title>");				 NewWindow.document.write (theName);				 NewWindow.document.write ("</title>
<link rel="shortcut icon" href="http://www.mat.ucsb.edu/~jiharu/new/favicon.ico" type="image/x-icon" />
